import axios from 'axios';
const URL = 'http://api.openweathermap.org/data/2.5/weather';
const API_KEY ='cd0214fd082631cb9307ea2553db0cb3';

export const fetchWeather = async (query)=>{
const {data} = await axios.get(URL, {
    params:{
        q:query,
        appid: API_KEY,
        units:'Metric'
    }
});
return data
}
