import React, {useState} from 'react';
import './App.css';
import {fetchWeather} from './api/fetchweather';
const App = ()=>{
    const [query, setQuery] = useState('');
    const [weather,setWeather] = useState({});
    const search = async (e)=>{
        if(e.key === 'Enter'){
            searchenter();
        }
    }
    const searchenter = async ()=>{
        if(query.match(/^[A-Za-z]+$/)){
            const data = await fetchWeather (query);
            console.log(data)
            if(data!==undefined){
                setWeather(data.data);
                setQuery('');
            }
            else{
                popup();
            }
        }
        else{
            popup();
        }

    }
    const popup =()=>{
        console.log('Invalid');
        setWeather('Invalid');
        console.log(weather);
    }
    return(
        <div className='main-container'>
            <div className="inputbox">
            <input id ="input" type='text' className="search" placeholder ='Enter City Name' value={query} onChange={(e) => setQuery(e.target.value)} onKeyPress={search}/>
            <button onClick={searchenter}><img src="data:image/png;base64," alt="Search"/></button>
            </div>
            {weather==="Invalid" && (
                <div className='city'>
                    <h1>Invalid City name</h1>
                </div>
            )}
            {weather.main && (
                <div className='city'>
                    <h2 className='city-name'>
                        <span>{weather.name}</span>
                        <sup>{weather.sys.country}</sup>
                    </h2>
                    <div className='city-temp'>
                        {Math.round(weather.main.temp)}
                        <sup>&deg;C</sup>
                    </div>
                    <div className='info'>
                        <img className='city-icon'src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} alt={weather.weather[0].description} />
                        <p>{weather.weather[0].description}</p>    
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
